import React from "react";
import { useSignupContext } from "./Context";

const handleSubmit = (event) => {
    event.preventDefault()
    const hasEmptyFields = requiredFields.some(field => formData[field] == undefined)

    if (hasEmptyFields) {
        return
    }

    setSubmitted(true)
}

function FormDisplay() {
    const {formData} = useSignupContext();
    const [submitted, setSubmitted] = useState(false);

    const requiredFields = ['username', 'email', 'password', 'passwordConfirm'];
    const handleSubmit = (event) => {
        event.preventDefault()
        const hasEmptyFields = requiredFields.some(field => formData[field] == undefined)
    
        if (hasEmptyFields) {
            return
        }
    
        setSubmitted(true)
    }

    return (
        <input type="text" disabled/>
    )
}

function CallToAction() {
    return (
        <h1>
            Bem vindo.
            <br />
            Comece sua jornada
            <br />
            com um clique
        </h1>
    )
}

export {FormDisplay, CallToAction}
