import React from "react";
import { useSignupContext } from "./Context";


function FormDisplay() {
    const {formData, updateFormData, clearFormData, submitted, setSubmitted} = useSignupContext();

    const FormClear = () => {
        clearFormData();
        Object.keys(formData).forEach((key) => {
            updateFormData(key, '')
        })
    }

    return (
        <>
            <input type="text" value={submitted ? formData.username : ''} disabled />
            <input type="email" value={submitted ? formData['email'] : ''} disabled />
            <input type="password" value={submitted ? formData['password'] : ''} disabled />
            <button type="button" className="clear-btn" onClick={FormClear}>Limpar</button>
        </>
    )
}

function CallToAction() {
    return (
        <h1>
            Bem vindo.
            <br />
            Comece sua jornada
            <br />
            com um clique
        </h1>
    )
}

export {FormDisplay, CallToAction}
