import './App.css';
import SignupForm from './SignupForm';
import { FormDisplay, CallToAction } from './FormDisplay';
import { useSignupContext } from './Context';
import { useState } from 'react';

function App() {
    const {formData, displayed} = useSignupContext();

    return (
        <main>
            <div>
                <div className='step-heading'>
                    <h1 className="signup-heading">Sign up</h1>
                    <h1 className="display-heading">Confirmar</h1>
                </div>
                <div className='main-container'>
                    <div className="signup-container">
                        <h1>Fa√ßa sua conta agora!</h1>
                        <SignupForm/>
                    </div>
                    <div className={ `display-container ${displayed ? 'visible' : ''}`}>
                        <h1>Confirme seus dados:</h1>
                        <FormDisplay/>
                    </div>
                    <div className={`cta-placeholder ${displayed ? 'not-visible' : ''}`}>
                        <CallToAction/>
                    </div>
                </div>
            </div>
        </main>
    )
}

export default App;
